mapper.insert:
    INSERT INTO exam.salary (
time, 
basic, 
insurance, 
userId
    )
    VALUES (
#{time}, 
#{basic}, 
#{insurance}, 
#{userId}
    );

mapper.select:
SELECT * FROM exam.salary
salary.id = #{id}
AND time LIKE "%"#{time}"%" AND basic LIKE "%"#{basic}"%" AND insurance LIKE "%"#{insurance}"%" AND userId LIKE "%"#{userId}"%"
